table = {}
for form in letterforms:
    if '|' in form:
        table[form[-2]] = form[:-3].split('|')
ROWS = len(list(table.values())[0])

def banner22(word):
    texto = ""
    for row in range(ROWS):
        for c in word:
            texto += table[c][row]
        texto += "\n"
    return texto


