class banner5x6:
    def get_banner(word,spacing:int=1):
        """Returns the banner as text without print it"""
        text = ""
        for row in range(6):
            for c in word:
                text += letters_clr5x6.get(c,' ')[row] + (" " * spacing)
            text += "\n"
        return text.rstrip()
    def print(word,spacing:int=1):
        """Prints the given text using the clr5x6 font"""
        print(banner5x6.get_banner(word))
    
